package com.github.tinosteinort.flda.stringlist.writer;

import com.github.tinosteinort.flda.stringlist.StringListAttribute;
import org.junit.Test;

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertNull;

public class BigDecimalAttributeWriterTest {

    private final BigDecimalAttributeWriter writer = new BigDecimalAttributeWriter();

    @Test public void writeBigValue() {
        final List<String> record = new ArrayList<>(1);
        record.add(null);
        final StringListAttribute<BigDecimal> attribute = new StringListAttribute<>(BigDecimal.class, 0);

        writer.write(record, attribute, new BigDecimal(".9999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999"));

        assertEquals(".9999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999", record.get(0));
    }

    @Test public void writeNull() {
        final List<String> record = new ArrayList<>(1);
        record.add(null);
        final StringListAttribute<BigDecimal> attribute = new StringListAttribute<>(BigDecimal.class, 0);

        writer.write(record, attribute, null);

        assertNull(record.get(0));
    }
}
